domainLanguage:
# Basic argumentation framework example
# Arguments
Argument("a");
Argument("b");
Argument("c");
Argument("d");
# Attacks between arguments
Attacks("a", "b");
Attacks("b", "c");
Attacks("c", "d");
Attacks("d", "a");

visualLanguage:
# Graph properties
Graph( id: "af", rankdir: "TB", layout: "dot");

# Node definitions - map domain arguments to visual nodes
Node( node_id: x, label: x, shape: "circle", border: "solid", fontsize: "14") :- Argument(x);

# Edge definitions - map domain attacks to visual edges
Edge(source_id: source, target_id: target, color: "black", style: "solid", arrowhead: "normal", arrowtail: "") :- Attacks(source, target);
