domainLanguage:
# Family relationships
Parent("Tom", "Bob");
Parent("Tom", "Liz");
Parent("Bob", "Ann");
Parent("Bob", "Pat");
Parent("Pat", "Jim");

# Define all people explicitly
Person(a):- Parent(a,b) | Parent(b,a);

visualLanguage:
# Graph properties
Graph( id: "family", rankdir: "TB", layout: "dot");

# Node definitions - map domain people to visual nodes
Node( node_id: x, label: x, shape: "circle", border: "solid", fontsize: "14") :- Person(x);

# Edge definitions - map domain parent relationships to visual edges
Edge(source_id: source, target_id: target, color: "black", style: "solid", arrowhead: "normal", arrowtail: "") :- Parent(source, target);
